        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1429336, 'Vandetanib', 'Omeprazole', 'Minor', 'The use of vandetanib with proton pump inhibitors may theoretically reduce its gastrointestinal absorption. Vandetanib exhibits pH-dependent solubility, with increased solubility at lower pH. The clinical significance is unknown. Dose adjustments are not required when vandetanib is administered with omeprazole or ranitidine.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
