INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (176878, 'Aminophylline', 'Zileuton', 'Moderate', 'Coadministration with zileuton may significantly increase the plasma concentrations of theophylline. The proposed mechanism is zileuton inhibition of theophylline metabolism via CYP450 1A2.', NULL, 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity.', NULL, 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity. When zileuton is initiated in patients receiving theophylline, the theophylline dosage should be reduced by approximately one-half and theophylline levels closely monitored. Similarly, when initiating therapy with theophylline in patients receiving zileuton, the maintenance dosage and/or dosing interval of theophylline should be adjusted accordingly and guided by serum theophylline levels. Patients should be advised to notify their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, headache, tremor, irritability, insomnia, seizures, palpitation, or arrhythmia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/177089/', '[1] "Product Information. Zylo Filmtab (zileuton)." Abbott Pharmaceutical, Abbott Park, IL.[2] Lau R "Drug interactions with zileuton." Lancet 349 (1997): 1479-80[3] Granneman GR, Braeckman RA, Locke CS, Cavanaugh JH, Dube LM, Awni WM "Effect of zileuton on theophylline pharmacokinetics." Clin Pharmacokinet 29(suppl 2 (1995): 77-83', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (176879, 'Zileuton', 'Astemizole', 'Moderate', 'Zileuton is believed to be a weak inhibitor of cytochrome P450 3A4 enzymes, of which astemizole is a known substrate. Increased levels of astemizole may increase the potential for cardiotoxic effects, including life-threatening torsade de pointes.', NULL, 'The coadministration of zileuton with astemizole should be avoided.', NULL, 'The coadministration of zileuton with astemizole should be avoided. Nonsedating antihistamines such as fexofenadine, loratadine, and cetirizine may be safer alternatives due to a lack of cardiotoxicity.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/177090/', '[1] "Product Information. Zylo Filmtab (zileuton)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Hismanal (astemizole)." Janssen Pharmaceutica, Titusville, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (176880, 'Zileuton', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/177091/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363026);
