    ) VALUES (1429864, 'Sapropterin', 'Methotrexate', 'Moderate', 'Drugs that affect folate metabolism may reduce the efficacy of sapropterin, which is a synthetic form of tetrahydrobiopterin (BH4). Folate antagonists can decrease tetrahydrobiopterin (BH4) levels by inhibiting the enzyme dihydropteridine reductase.', NULL, 'Caution is advised if sapropterin is used in combination with folate antagonists.', NULL, 'Caution is advised if sapropterin is used in combination with folate antagonists. The potential for diminished pharmacologic effects of sapropterin should be considered.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
