INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429899, 'Tedizolid', 'Methotrexate', 'Moderate', 'Coadministration with oral tedizolid may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tedizolid.', 'DDInter', 1767317476, 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index. If coadministration cannot be avoided, monitor for adverse reactions related to concomitantly administered BCRP substrate.', 'Absorption', 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
