INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429905, 'Teriflunomide', 'Methotrexate', 'Major', 'Coadministration of methotrexate and leflunomide has been associated with an increased risk for hepatotoxicity. The risk probably extends to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317476, 'The use of leflunomide or teriflunomide in combination with methotrexate is not recommended. Since the active metabolite of leflunomide may remain in the body for up to 2 years, the cholestyramine washout procedure is recommended when changing from leflunomide to methotrexate. Liver enzyme levels should be obtained before beginning leflunomide/teriflunomide and monthly during therapy. If ALT increases to 2 to 3 times normal, the dose should be reduced and liver enzymes monitored weekly. If ALT increases to more than 3 times normal, therapy should be discontinued and the washout procedure should be instituted.', 'Synergism', 'The use of leflunomide or teriflunomide in combination with methotrexate is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
