INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429923, 'Trichophyton mentagrophytes', 'Methotrexate', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429924, 'Trimethoprim', 'Methotrexate', 'Major', 'Coadministration of methotrexate with trimethoprim may increase the risk of severe myelosuppression and megaloblastic anemia due to potential additive effects resulting from inhibition of dihydrofolate reductase by both drugs. The interaction has been reported primarily with sulfamethoxazole-trimethoprim, but has also occurred with trimethoprim alone.', 'DDInter', 1767317476, 'Concomitant use of methotrexate with trimethoprim should be avoided if possible. Some clinicians consider the use of methotrexate with low-dose sulfamethoxazole-trimethoprim (e.g., for the prophylaxis of Pneumocystis jiroveci pneumonia) to be acceptable; however, close monitoring for hematologic toxicity is recommended, particularly in the elderly. Complete blood counts, liver enzymes, and serum creatinine levels should be performed at baseline and periodically during treatment. Folate supplementation may be required in some cases. Patients should be advised to notify their doctor if they experience signs and symptoms of bone marrow depression or anemia such as fever, chills, sore throat, easy bruising or bleeding, pallor, dizziness, fatigue, lethargy, sore mouth or tongue, and tingling in hands or feet.', 'Synergism', 'Concomitant use of methotrexate with trimethoprim should be avoided if possible.', NULL, NULL, 'Major', 'pharmacodynamic');
