INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429929, 'Trovafloxacin', 'Methotrexate', 'Moderate', 'Coadministration with ciprofloxacin or other quinolones may increase the plasma concentrations of methotrexate. The exact mechanism of interaction is unknown but may involve competitive inhibition of the renal tubular secretion of methotrexate.', 'DDInter', 1767317476, 'Caution is advised if quinolones must be used with methotrexate. The authors of the case report recommend that ciprofloxacin and related antibiotics not be coadministered with high-dose methotrexate. During concomitant use, patients should be monitored for altered pharmacologic effects of both drugs and the dosage(s) adjusted accordingly if necessary.', 'Excretion', 'Caution is advised if quinolones must be used with methotrexate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
