INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429949, 'Warfarin', 'Methotrexate', 'Minor', 'case report describes a 62-year-old man treated with warfarin whose prothrombin times increased by 50% to 100% during three cycles of a chemotherapeutic regimen consisting of cyclophosphamide, doxorubicin, etoposide, mechlorethamine, vincristine, procarbazine, methotrexate, and prednisone. He developed a subconjunctival hemorrhage on day 1 of the first cycle. The prothrombin time returned to normal within one week of discontinuation of warfarin. Due to the complexity of the chemotherapeutic regimen, a causal relationship to any of the medications or combination of medications cannot be established.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
