INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429959, 'Epirubicin', 'Oxandrolone', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', 'DDInter', 1767317476, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429960, 'Fludrocortisone', 'Oxandrolone', 'Moderate', 'Concomitant use of androgens with adrenocorticotropic hormone (ACTH) or corticosteroids may result in increased fluid retention and edema due to additive steroidal effects.', 'DDInter', 1767317476, 'Caution is advised if androgens are used in combination with ACTH or corticosteroids, particularly in patients with conditions that may be aggravated by fluid retention such as cardiovascular, renal, or hepatic diseases.', 'Synergism', 'Caution is advised if androgens are used in combination with ACTH or corticosteroids, particularly in patients with conditions that may be aggravated by fluid retention such as cardiovascular, renal, or hepatic diseases.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1429961, 'Fluvastatin', 'Oxandrolone', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of fluvastatin. According to the prescribing information, fluvastatin is primarily metabolized by CYP450 2C9 (approximately 75%) and, to a much lesser extent, by CYP450 2C8 and 3A4 (approximately 5% and 20%, respectively).', 'DDInter', 1767317476, 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors. The lowest starting dosage of fluvastatin is recommended, then titrated as needed based on clinical response and tolerance. Alternatively, pravastatin is not metabolized by CYP450 2C9 and may be a reasonable substitute for fluvastatin. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
