INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (177729, 'Entrectinib', 'Clotrimazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', NULL, 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors. Pharmacologic response to entrectinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the entrectinib dosage adjusted as necessary in accordance with the product labeling. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/177940/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[4] Dimartini A "Isoniazid, tricyclics and the ''''cheese reaction''''." Int Clin Psychopharmacol 10 (1995): 197-8[5] "Product Information. INH (isoniazid)." Ciba Pharmaceuticals, Summit, NJ.[6] Smith CK, Durack DT "Isoniazid and reaction to cheese." Ann Intern Med 88 (1978): 520-1[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Self TH, Chrisman CR, Baciewicz AM, Bronze MS "Isoniazid drug and food interactions." Am J Med Sci 317 (1999): 304-11[9] Uragoda CG, Kottegoda SR "Adverse reactions to isoniazid on ingestion of fish with a high histamine content." Tubercle 58 (1977): 83-9', 1767365209);
