INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (177863, 'Eravacycline', 'Enzalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of eravacycline, which undergoes oxidation by CYP450 3A4 and flavin monoxygenase (FMO).', NULL, 'Eravacycline dosage should be increased to 1.', NULL, 'Eravacycline dosage should be increased to 1.5 mg/kg every 12 hours when used with potent CYP450 3A4 inducers. No dosage adjustment is warranted when eravacycline is used with a weak or moderate inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178074/', '[1] "Product Information. Xerava (eravacycline)." Tetraphase Pharmaceuticals, Inc, Watertown, MA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365209);
