INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (177959, 'Enzalutamide', 'Prednisolone', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178170/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.[3] Benoist G, van Oort I, et al "Drug-drug interaction potential in men treated with enzalutamide: Mind the gap." Br J Clin Pharmacol 0 (2017): epub[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (177960, 'Enzalutamide', 'Pretomanid', 'Major', 'Coadministration of pretomanid with strong or moderate CYP450 3A4 inducers may decrease the plasma concentrations and antimicrobial effects of pretomanid, which has been shown to be a substrate of the isoenzyme.', NULL, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided.', NULL, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided. Dosage adjustments as well as clinical and laboratory monitoring of pretomanid should be considered whenever a strong or moderate CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178171/', '[1] "Product Information. Pretomanid (pretomanid)." The Global Alliance for TB Drug Development, New York, NY.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Pretomanid (pretomanid)." The Global Alliance for TB Drug Development, New York, NY.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (177961, 'Enzalutamide', 'Primaquine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178172/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (