INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (177991, 'Aminophylline', 'Ephedrine', 'Minor', 'Ephedrine-theophylline or ephedrine-dyphylline combinations are used for the treatment of asthma but the efficacy of the combination has been questioned. This combination may lead to increased xanthine side effects. The mechanism is unknown, but may be related to synergistic pharmacologic effects. Patients using this combination should be closely monitored for side effects such as nausea, vomiting, tachycardia, nervousness, or insomnia. If side effects are noted, the dosage of the xanthine may need to be decreased.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178202/', '[1] Badiei B, Faciane J, Sly M "Effect of throphylline, ephedrine and theri combination upon exercise-induced airway obstruction." Ann Allergy 35 (1975): 32-6[2] Weinberger M, Bronsky E, Bensch GW, Bock GN, Yecies JJ "Interaction of ephedrine and theophylline." Clin Pharmacol Ther 17 (1975): 585-92[3] Tinkelman DG, Avner SE "Ephedrine therapy in asthmatic children. Clinical tolerance and absence of side effects." JAMA 237 (1977): 553-7[4] Weinberger MM, Brousky EA "Evaluation of oral bronchodilator therapy in asthmatic children: bronchodilators in asthmatic children." J Pediatr 84 (1974): 421-7[5] Sims JA, doPico GA, Reed CE "Bronchodilating effect of oral theophylline-ephedrine combination." J Allergy Clin Immunol 62 (1978): 15-21', 1767365209);
