INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430305, 'Paromomycin', 'Nadolol', 'Minor', 'Orally administered aminoglycosides may increase the gastrointestinal absorption and elimination of nadolol. The clinical implications of this interaction are unknown. No special precautions appear to be necessary.', 'DDInter', 1767317476, '-', 'Absorption, Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430306, 'Paromomycin', 'Neostigmine', 'Moderate', 'Aminoglycosides may antagonize the effects of anticholinesterase agents such as neostigmine and pyridostigmine. The interaction has been established between aminoglycosides and depolarizing and nondepolarizing muscle relaxants and is also predicted to occur with anticholinesterase agents. Anticholinesterase agents inhibit cholinesterase activity and lead to prolonged and intensified muscarinic and nicotinic effects of acetylcholine. Aminoglycosides appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. They also possess neuromuscular blocking activity, which may interfere with neuromuscular transmission by inhibiting the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. This effect on neuromuscular transmission may be particularly significant in patients with myasthenia gravis.', 'DDInter', 1767317476, 'Caution and close monitoring for the efficacy of the anticholinesterase agent is recommended if it is to be used concomitantly with aminoglycosides, particularly in patients with myasthenia gravis. Concomitant use in these patients should be avoided if possible. If coadministration is considered necessary, the dose of the anticholinesterase agent may need to be increased.', 'Antagonism', 'Caution and close monitoring for the efficacy of the anticholinesterase agent is recommended if it is to be used concomitantly with aminoglycosides, particularly in patients with myasthenia gravis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
