INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430317, 'Typhoid vaccine (live)', 'Paromomycin', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 1767317476, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430318, 'Valaciclovir', 'Paromomycin', 'Moderate', 'The concurrent use of valaciclovir with other nephrotoxic drugs may increase the risk of renal toxicity. Patients who are elderly, debilitated, dehydrated, or who have preexisting renal dysfunction may have a higher risk of developing adverse reactions. Cases of acute renal failure have been reported.', 'DDInter', 1767317476, 'Close clinical and laboratory monitoring of renal function and drug serum concentrations (if clinically appropriate) is recommended.', 'Synergism', 'Close clinical and laboratory monitoring of renal function and drug serum concentrations (if clinically appropriate) is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
