INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430389, 'Acetohexamide', 'Pegvisomant', 'Moderate', 'Treatment with pegvisomant may increase glucose tolerance in some diabetic patients, potentially reducing their requirements for insulin and other antidiabetic agents. The mechanism is pegvisomant antagonism of endogenous growth hormone, the latter of which normally decreases insulin sensitivity and opposes the effects of insulin on carbohydrate metabolism. However, clinically relevant hypoglycemia was not observed in diabetic patients who were treated with pegvisomant in premarketing trials.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended when pegvisomant is used in patients receiving insulin or other antidiabetic agents, and the dosages of these agents adjusted as necessary. Likewise, patients may be at risk for hyperglycemia when pegvisomant is withdrawn from their therapeutic regimen.', 'Synergism', 'Close clinical monitoring of glycemic control is recommended when pegvisomant is used in patients receiving insulin or other antidiabetic agents, and the dosages of these agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
