INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430438, 'Pembrolizumab', 'Lenalidomide', 'Major', 'Coadministration of thalidomide or its analogs with a programmed death receptor-1 (PD-1) or programmed death ligand-1 (PD-L1) blocking antibody may increase the risk of mortality in patients with multiple myeloma. The mechanism has not been established.', 'DDInter', 1767317476, 'Concomitant use of a PD-1 or PD-L1 blocking antibody with thalidomide or a thalidomide analog plus dexamethasone in the treatment of multiple myeloma is not recommended outside of controlled clinical trials.', 'Others', 'Concomitant use of a PD-1 or PD-L1 blocking antibody with thalidomide or a thalidomide analog plus dexamethasone in the treatment of multiple myeloma is not recommended outside of controlled clinical trials.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430439, 'Pembrolizumab', 'Methylprednisolone', 'Moderate', 'INTERVAL: Corticosteroids may affect the pharmacodynamic effects and efficacy of immune checkpoint inhibitors (ICI) such as programmed cell death-1 (PD-1) or programmed death ligand-1 inhibitors (PD-L1). The mechanism of this interaction is unknown; however, it has been theorized that the immunosuppressive properties of corticosteroids and the potential effect on T-cell function may decrease the efficacy of ICIs.', 'DDInter', 1767317476, 'The manufacturers'' product labeling should be consulted for specific recommendations. If clinically possible, the use of systemic corticosteroids should be avoided at the time of or within 30 days of starting therapy with an ICI, e.g., durvalumab, nivolumab, pembrolizumab, ipilimumab, cemiplimab, or atezolizumab. Corticosteroids may be used for the treatment of immune-mediated reactions after starting an ICI. Some manufacturers advise that corticosteroids may be used as premedication, when the ICI is used in combination with chemotherapy, as antiemetic prophylaxis, and/or to alleviate chemotherapy-related adverse effects.', 'Antagonism', 'The manufacturers'' product labeling should be consulted for specific recommendations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430440, 'Pomalidomide', 'Pembrolizumab', 'Major', 'Coadministration of thalidomide or its analogs with a programmed death receptor-1 (PD-1) or programmed death ligand-1 (PD-L1) blocking antibody may increase the risk of mortality in patients with multiple myeloma. The mechanism has not been established.', 'DDInter', 1767317476, 'Concomitant use of a PD-1 or PD-L1 blocking antibody with thalidomide or a thalidomide analog plus dexamethasone in the treatment of multiple myeloma is not recommended outside of controlled clinical trials.', 'Others', 'Concomitant use of a PD-1 or PD-L1 blocking antibody with thalidomide or a thalidomide analog plus dexamethasone in the treatment of multiple myeloma is not recommended outside of controlled clinical trials.', NULL, NULL, 'Major', 'pharmacodynamic');
