INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178021, 'Isoetharine', 'Ephedrine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', NULL, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', NULL, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Ketorolac, Homatropine, Atropine, Scopolamine, Methscopolamine, Tropicamide, Mupirocin, Mometasone furoate, Vitamin A, Olopatadine, Cromoglicic acid, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178232/', '[1] Lowe MD, Rowland E, Brown MJ, Grace AA "Beta(2) adrenergic receptors mediate important electrophysiological effects in human ventricular myocardium." Heart 86 (2001): 45-51[2] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[3] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[4] "Product Information. Maxair (pirbuterol)." 3M Pharmaceuticals, St. Paul, MN.[5] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[7] Wong CS, Pavord ID, Williams J, Britton JR, Tattersfield AE "Bronchodilator, cardiovascular, and hypokalaemic effects of fenoterol, salbutamol, and terbutaline in asthma." Lancet 336 (1990): 1396-9[8] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[9] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim, Ridgefield, CT.[10] "Product Information. Severent (salmeterol)." Glaxo Wellcome, Research Triangle Park, NC.[11] "Product Information. Xopenex (levalbuterol)." Sepracor, Marlborough, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178022, 'Ephedrine', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index. Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', NULL, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', NULL, 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', 'Synergism', 'Halothane, Thiopental, Alfentanil, Remifentanil, Isoflurane, Sodium oxybate, Methohexital, Fentanyl, Desflurane, Nitrous oxide, Sufentanil, More', 'Homatropine, Tropicamide, Methscopolamine, Ketorolac, Flunisolide, Betamethasone, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178233/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Ketalar (ketamine)." JHP Pharmaceuticals, Saddle River, NJ.[4] Peltoniemi MA, Saari TI, Hagelberg NM, Laine K, Neuvonen PJ, Olkkola KT "S-ketamine concentrations are greatly increased by grapefruit juice." Eur J Clin Pharmacol 68 (2012): 979-86[5] Cerner Multum, Inc. "UK Summary of Product Characte