INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178023, 'Ephedrine', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of ephedrine by blocking beta-1 adrenergic receptors in the heart. Noncardioselective beta-blockers can antagonize the bronchodilating effects of ephedrine by blocking beta-2 adrenergic receptors in smooth muscles of the bronchial tree. The interaction is less likely to occur with cardioselective beta-blockers, which generally have little effect on beta-2 adrenergic receptors at therapeutic dosages.', NULL, 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations. Noncardioselective beta-blockers, including ophthalmic formulations, should generally be avoided in patients using ephedrine-containing preparations for bronchospastic diseases. If beta-blocker therapy is necessary, an agent with beta-1 selectivity (e.g., atenolol, metoprolol, betaxolol) is considered safer. However, caution is advised, especially with higher dosages of the beta-blocker.', 'Antagonism', 'Ketorolac, Homatropine, Atropine, Scopolamine, Methscopolamine, Tropicamide, Flunisolide, Betamethasone, Triamcinolone, Azelastine, Ciclesonide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178234/', '[1] "Product Information. Ephedrine Sulfate (ephedrine)." Akorn Inc, Buffalo Grove, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178024, 'Ephedrine', 'Lindane', 'Moderate', 'Lindane penetrates human skin and has the potential to cause central nervous system toxicity. Seizures have been reported after excessive use or oral ingestion of lindane. There may be a theoretical risk of increased seizure potential when lindane is used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, theophylline).', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Lindane should be used according to recommended dosage and directions for application.', 'Synergism', 'Disulfiram', 'Ketorolac, Homatropine, Atropine, Scopolamine, Methscopolamine, Tropicamide, Phenylephrine, Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/