INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178147, 'Gadobenic acid', 'Epirubicin', 'Moderate', 'Gadobenate dimeglumine may compete with other substrates of the canalicular multispecific organic anion transporter, also known as cMOAT or MRP2, which is an endogenous transporter protein that works in coordination with the conjugation process to facilitate drug excretion. In solid tumor cells, overexpression of MRP2 confers resistance to a wide variety of anticancer chemotherapeutic agents. Substrates of MRP2 include anthracyclines, protease inhibitors, taxanes, vinca alkaloids, cisplatin, etoposide, methotrexate, and tamoxifen. Theoretically, competition for MRP2 transport may interfere with the clearance of these drugs and/or gadobenate dimeglumine.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2. Patients should be monitored for potentially increased pharmacologic effects of gadobenate dimeglumine as well as the coadministered drug(s).', 'Excretion', 'Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin', 'Gadoversetamide, Gadobutrol, Gadofosveset trisodium, Gadopentetic acid, Gadoxetic acid, Gadoteric acid, Gadodiamide, Gadoteridol, Ferumoxsil', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178358/', '[1] "Product Information. Multihance (gadobenate dimeglumine)." Bracco Diagnostics Inc, Princeton, NJ.[2] Huisman MT, Smit JW, Crommentuyn KM, et al. "Multidrug resistance protein 2 (MRP2) transports HIV protease inhibitors, and transport can be enhanced by other drugs." AIDS 16 (2002): 2295-2301', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178148, 'Galantamine', 'Epirubicin', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin', 'Memantine, Ginkgo biloba, Aducanumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178359/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178149, 'Ganciclovir', 'Epirubicin', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Valrubicin', 'Paritaprevir, Glecaprevir, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, Etravirine, Dolutegravir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynam