INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430640, 'Pemetrexed', 'Pentamidine', 'Moderate', 'Coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of pemetrexed, which is primarily eliminated unchanged by the kidney via glomerular filtration and active tubular secretion.', 'DDInter', 1767317476, 'Caution is advised if pemetrexed is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of pemetrexed such as bone marrow suppression should be considered. Renal function should be closely monitored during therapy.', 'Excretion', 'Caution is advised if pemetrexed is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430641, 'Perflutren', 'Pentamidine', 'Moderate', 'Perflutren lipid microsphere suspension (Definity) has been reported to cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if perflutren lipid microsphere suspension is used in combination with other drugs that can prolong the QT interval. Please refer to the prescribing information for these drugs for more information. No data are available regarding the effect of perflutren protein-type A microsphere suspension (Optison) on the QT interval of the electrocardiogram.', 'Synergism', 'Caution is recommended if perflutren lipid microsphere suspension is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
