INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1430707, 'Tolazamide', 'Pentamidine', 'Moderate', 'Pentamidine may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of pentamidine has been associated with disturbances in blood glucose homeostasis due to direct toxic effects on beta cells of the pancreas. Hypoglycemia, which may be severe and/or prolonged, as well as hyperglycemia and insulin-dependent diabetes mellitus, the latter of which may be irreversible, have been reported.', 'DDInter', 1767317476, 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during pentamidine therapy, patients should initiate appropriate remedial therapy immediately and contact their physician. Dosage adjustments may be required if an interaction is suspected.', 'Antagonism', 'Blood glucose should be monitored closely during and after pentamidine therapy in patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
