INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178533, 'Eravacycline', 'Typhoid vaccine (live)', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178744/', '[1] UK government "Typhoid. Available from: URL: https://www.gov.uk/government/uploads/system/uploads/attachment_data/file/148512/Green-Book-Chapter-33-dh_125348.pdf." ([cited 2014]):[2] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] CDC. Centers for Disease Control and Prevention "Typhoid & Paratyphoid Fever. Available from: URL: http://wwwnc.cdc.gov/travel/yellowbook/2014/chapter-3-infectious-diseases-related-to-travel/typhoid-and-paratyphoid-fever." ([2013]):', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178534, 'Erdafitinib', 'Aluminum hydroxide', 'Major', 'Coadministration with agents that can alter serum phosphate levels may affect the initial dosage determination of erdafitinib. The mechanism appears to be related to the pharmacodynamic effects of fibroblast growth factor receptor (FGFR) inhibition by erdafitinib. Inhibition of the FGFR receptor has been shown to lead to an increase in serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels. After the initial dose increase period, an oral phosphate binder may be appropriate in the management of patients with serum phosphate levels greater than 7 mg/dL. If concomitant use with agents known to increase serum phosphate levels is required after this initial dose increase period, close monitoring of patient tolerability and serum phosphate levels is recommended.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178745/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178535, 'Amprenavir', 'Erdafitinib', 'Major', 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 may increase the plasma concentrations and risk of toxicity of erdafitinib, which is a substrate of these isoenzymes.', NULL, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided.', NULL, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided. If concomitant use is unavoidable, patients should be closely monitored for the development of adverse effects. Dosage adjustments as well as clinical and laboratory monitoring of erdafitinib should be considered whenever a CYP450 2C9 or 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178746/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767365209);
