INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178560, 'Fosphenytoin', 'Erdafitinib', 'Major', 'Coadministration with potent inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', NULL, 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', NULL, 'Concomitant use of erdafitinib with potent inducers of CYP450 2C9 or 3A4 should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178771/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365209);
