INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178588, 'Ergocalciferol', 'Butabarbital', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178799/', '[1] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178589, 'Ergocalciferol', 'Digitoxin', 'Moderate', 'Excessive dosing of vitamin D can induce hypercalcemia, which may increase the risk of digitalis toxicity and serious arrhythmias due to additive inotropic effects.', NULL, 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs.', NULL, 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs. Serum digoxin, calcium, and phosphorus levels should be closely monitored, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178800/', '[1] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[2] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.[5] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178590, 'Ergocalciferol', 'Dihydrotachysterol', 'Major', 'Due to a narrow therapeutic index, vitamin D analogs given in combination with each other or with pharmacologic doses of vitamin D may demonstrate additive effects resulting in toxicity manifested as hypercalcemia, hypercalciuria, and hyperphosphatemia. Progressive hypercalcemia due to overdosage of vitamin D and its metabolites can be dangerous and sufficiently severe as to require emergency attention. Acute hypercalcemia may exacerbate tendencies for cardiac arrhythmias and seizures and may potentiate the action of digitalis drugs. Chronic hypercalcemia can lead to generalized vascular calcification, nephrocalcinosis, and calcifications of the cornea or other soft tissues. Immobilized patients are particularly vulnerable to the risk of hypercalcemia.', NULL, 'Vitamin D analogs should not be used in combination with each other or with pharmacologic doses of vitamin D.', NULL, 'Vitamin D analogs should not be used in combination with each other or with pharmacologic doses of vitamin D. Serum calcium and phosphorus levels must be closely monitored during therapy with a vitamin D analog, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia. Late symptoms may include polyuria, polydipsia, weight loss, nocturia, conjunctivitis, photophobia, rhinorrhea, pruritus, hyperthermia, decreased libido, and cardiac arrhythmias. Radiographic evaluation of suspect anatomical regions may be useful in the early detection of hypercalcemia. If hypercalcemia develops, treatment with vitamin D and any calcium product should be stopped immediately.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178801/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[3] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.[4] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[5] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365209);
