INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178813, 'Ibrutinib', 'Erlotinib', 'Moderate', 'Coadministration with ibrutinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter. In vitro studies indicate that ibrutinib is not a substrate for P-gp, and systemic ibrutinib is unlikely to be an inhibitor of P-gp at clinical doses.', NULL, 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range. Dosage reduction and/or dosage interval adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ibrutinib is added to or withdrawn from therapy. Some authorities recommend that narrow therapeutic range P-gp substrates, such as digoxin, should be administered at least 6 hours before or after ibrutinib.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179024/', '[1] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Pohjola-Sintonen S, Viitasalo M, Toivonene L, Neuvonen P "Torsades de pointes after terfenadine-itraconazole interaction." BMJ 306 (1993): 186[4] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[5] Zimmermann M, Duruz H, Guinand O, et al "Torsades de Pointes after treatment with terfenadine and ketoconazole." Eur Heart J 13 (1992): 1002-3[6] Honig PK, Wortham DC, Lazarev A, Cantilena LR "Grapefruit juice alters the systemic bioavailability and cardiac repolarization of terfenadine in poor metabolizers of terfenadine." J Clin Pharmacol 36 (1996): 345-51[7] Honig PK, Wortham DC, Zamani K, et al "Terfenadine-ketoconazole interaction: pharmacokinetic and electrocardiographic consequences." JAMA 269 (1993): 1513-8[8] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[9] Benton RE, Honig PK, Zamani K, Cantilena LR, Woosley RL "Grapefruit juice alters terfenadine pharmacokinetics resulting in prolongation of repolarization on the electrocardiogram." Clin Pharmacol Ther 59 (1996): 383-8[10] Mathews DR, McNutt B, Okerholm R, et al "Torsades de pointes occurring in association with terfenadine use." JAMA 266 (1991): 2375-6[11] Clifford CP, Adams DA, Murray S, Taylor GW, Wilkins MR, Boobis AR, Davies DS "Pharmacokinetic and cardiac effects of terfenadine after inhibition of its metabolism by grapefruit juice." Br J Clin Pharmacol 42 (1996): p662[12] Hsieh MH, Chen SA, Chiang CE, et al. "Drug-induced torsades de pointes in one patient with congenital long QT syndrome." Int J Cardiol 54 (1996): 85-8[13] Woosley RL "Cardiac actions of antihistamines." Annu Rev Pharmacol Toxicol 36 (1996): 233-52[14] Rau SE, Bend JR, Arnold JMO, Tran LT, Spence JD, Bailey DG "Grapefruit juice terfenadine single-dose interaction: Magnitude, mechanism, and relevance." Clin Pharmacol Ther 61 (1997): 401-9[15] Honig PK, Woosley RL, Zamani K, Conner DP, Cantilena LR Jr "Changes in the pharmacokinetics and electrocardiographic pharmacodynamics of terfenadine with concomitant administration of erythromycin." Clin Pharmacol Ther 52 (1992): 231-8[16] Monahan BP, Ferguson CL, Killeavy ES, et al "Torsades de pointes occurring in association with terfenadine use.