INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178826, 'Erlotinib', 'Ranitidine (bismuth citrate)', 'Moderate', 'INTERVAL: Concurrent administration of agents that increase gastric pH such as H2-receptor antagonists may decrease the oral bioavailability of erlotinib and reduce its concentrations in plasma. The solubility of erlotinib decreases when the pH is over 5, resulting in reduced absorption.', NULL, 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible.', NULL, 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible. If acid-suppression therapy is required during erlotinib treatment, an H2-receptor antagonist such as ranitidine may be considered. Available pharmacokinetic data suggest that erlotinib should be administered once a day, at least 2 hours before or 10 hours after dosing of the H2-receptor antagonist. Additionally, the lowest effective dosage of the H2-receptor antagonist should be prescribed. It may be appropriate to avoid using cimetidine for long-term acid suppression, as it may inhibit the CYP450 3A4-mediated metabolism of erlotinib and increase the risk of toxicity.', 'Metabolism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179037/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Duong S, Leung M "Should the concomitant use of erlotinib and acid-reducing agents be avoided? The drug interaction between erlotinib and acid-reducing agents." J Oncol Pharm Pract 17 (2011): 448-52[4] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.[5] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007): 3731-7[6] "Product Information. Tarceva (erlotinib)." Genentech, South San Francisco, CA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (178827, 'Erlotinib', 'Riociguat', 'Moderate', 'Coadministration with inhibitors of CYP450 1A1 may theoretically increase the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme to its main active metabolite (1/10th to 1/3rd the pharmacologic activity of riociguat). Increased levels of riociguat may increase the risk for hypotension.', NULL, 'Caution is advised if riociguat is prescribed in combination with an inhibitor of CYP450 1A1.', NULL, 'Caution is advised if riociguat is prescribed in combination with an inhibitor of CYP450 1A1. Blood pressure should be monitored, and a dose reduction of riociguat should be considered if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179038/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Ziagen (abacavir)." Glaxo Wellcome, Research Triangle Pk, NC.[5] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767365209);
