        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1431060, 'Perindopril', 'Pergolide', 'Moderate', 'One patient, whose hypertension had been treated with lisinopril for three years, experienced a severe hypotensive episode after receiving a 0.05 mg dose of pergolide. The mechanism is unknown and causality has not been clearly established.', NULL, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors.', NULL, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors. Dose adjustments may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
