INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179195, 'Esketamine', 'Levacetylmethadol', 'Major', 'The central nervous system and respiratory depressant effects of levomethadyl acetate may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', NULL, 'Caution and dosage adjustments are advisable if CNS depressants are prescribed to patients on levomethadyl acetate maintenance.', NULL, 'Caution and dosage adjustments are advisable if CNS depressants are prescribed to patients on levomethadyl acetate maintenance. Alcohol use should be avoided, and patients should be advised to avoid driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how the medications affect them. Patients who are known to abuse sedatives, tranquilizers, antidepressants, benzodiazepines, and alcohol should be warned of the risk of serious and potentially fatal overdose if these substances are taken concurrently.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179406/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179196, 'Esketamine', 'Mazindol', 'Major', 'Coadministration with psychostimulants (e.g., amphetamines) or monoamine oxidase inhibitors (MAOIs) may potentiate the hypertensive effects of esketamine.', NULL, 'Caution is advised and blood pressure should be closely monitored during concomitant use of esketamine with psychostimulants or MAOIs.', NULL, 'Caution is advised and blood pressure should be closely monitored during concomitant use of esketamine with psychostimulants or MAOIs. All patients receiving esketamine should have BP assessed prior to administration. If BP is elevated (e.g., >140 mmHg systolic, >90 mmHg diastolic), a delay in esketamine treatment may be necessary, taking into consideration the benefits versus risks in individual patients. BP should be monitored for at least 2 hours after esketamine administration, starting at approximately 40 minutes post-dose and subsequently as clinically warranted. In patients with a history of hypertensive encephalopathy, more intensive monitoring is warranted due to increased risk for developing encephalopathy with even small increases in BP. If at any point BP is elevated and remains high, promptly seek assistance from practitioners experienced in BP management. Patients experiencing symptoms of a hypertensive crisis (e.g., chest pain, shortness of breath) or hypertensive encephalopathy (e.g., sudden severe headache, visual disturbances, seizures, diminished consciousness or focal neurological deficits) should be immediately referred for emergency care.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179407/', '[1] "Product Information. Spravato (esketamine)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363026);
