INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179324, 'Eslicarbazepine', 'Polythiazide', 'Moderate', 'Coadministration with other medications that can lower serum sodium levels such as diuretics, selective serotonin reuptake inhibitors (SSRIs), and serotonin-noradrenaline reuptake inhibitors (SNRIs) may potentiate the risk of hyponatremia associated with eslicarbazepine.', NULL, 'Routine monitoring of serum sodium and chloride levels should be considered during maintenance treatment with eslicarbazepine acetate and concomitant use of other medications known to decrease serum sodium levels.', NULL, 'Routine monitoring of serum sodium and chloride levels should be considered during maintenance treatment with eslicarbazepine acetate and concomitant use of other medications known to decrease serum sodium levels. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hyponatremia such as nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, and unsteadiness (which may lead to falls). More severe and/or acute cases may include hallucination, syncope, seizure, coma, respiratory arrest, and death. Depending on the severity of hyponatremia, dosage reduction or discontinuation of eslicarbazepine acetate and other medications may be required, and appropriate medical intervention instituted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179535/', '[1] "Product Information. Aptiom (eslicarbazepine)." Sunovion Pharmaceuticals Inc, Marlborough, MA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179325, 'Eslicarbazepine', 'Pretomanid', 'Major', 'Coadministration of pretomanid with strong or moderate CYP450 3A4 inducers may decrease the plasma concentrations and antimicrobial effects of pretomanid, which has been shown to be a substrate of the isoenzyme.', NULL, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided.', NULL, 'The use of pretomanid with strong or moderate CYP450 3A4 inducers should be avoided. Dosage adjustments as well as clinical and laboratory monitoring of pretomanid should be considered whenever a strong or moderate CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179536/', '[1] "Product Information. Pretomanid (pretomanid)." The Global Alliance for TB Drug Development, New York, NY.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Pretomanid (pretomanid)." The Global Alliance for TB Drug Development, New York, NY.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179326, 'Eslicarbazepine', 'Progesterone (topical)', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', NULL, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179537/', '[1] Back DJ, Breckenridge AM, Crawford FE, MacIver M, Orne ML, Rowe PH "Interindividual variation and drug interactions with hormonal steroid contraceptives." Drugs 21 (1981): 46-61[2] van Dijke CP, Weber JC "Interaction between oral contraceptives and griseofulvin." Br Med J (Clin Res Ed) 288 (1984): 1125-6[3] Dickinson BD, Altman RD, Nielsen NH, Sterling ML "Drug interactions between oral contraceptives and antibiotics." Obstet Gynecol 98(5 P