        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1431111, 'Doxorubicin (liposomal)', 'Trastuzumab', 'Major', 'The use of trastuzumab and anthracyclines in combination has been associated with a high risk of cardiotoxicity. Trastuzumab and agents in the anthracycline class are individually cardiotoxic and may have additive effects during coadministration.', NULL, 'Trastuzumab and cardiotoxic agents such as anthracyclines should not be used in combination except in settings where cardiac monitoring is possible.', NULL, 'Trastuzumab and cardiotoxic agents such as anthracyclines should not be used in combination except in settings where cardiac monitoring is possible. The use of anthracycline-based therapy should be avoided, if possible, for up to 7 months after stopping trastuzumab. If anthracyclines must be used sooner, the patient''s cardiac function should be closely monitored.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
