    ) VALUES (1431134, 'Trastuzumab', 'Niraparib', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with niraparib. Myelodysplastic syndrome (MDS) and acute myeloid leukemia (AML) have been reported in patients treated with niraparib.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', NULL, 'Caution is advised if niraparib is prescribed with other myelosuppressive agents.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
