    ) VALUES (1431559, 'Glimepiride', 'Aloe Vera Leaf', 'Moderate', 'Concomitant use of oral products containing aloe vera gel may potentiate the hypoglycemic effect of insulin and other antidiabetic agents.', NULL, 'Until more data are available, caution is advised when aloe vera is administered orally to patients receiving insulin or other antidiabetic agents due to the potential for increased risk of hypoglycemia.', NULL, 'Until more data are available, caution is advised when aloe vera is administered orally to patients receiving insulin or other antidiabetic agents due to the potential for increased risk of hypoglycemia. Patients should be monitored for changes in diabetic medication requirements.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
