    ) VALUES (1431560, 'Glimepiride', 'Lipoic acid', 'Minor', 'Theoretically, alpha-lipoic acid may potentiate the hypoglycemic effect of insulin and oral antidiabetic agents. Alpha-lipoic acid has been shown to improve insulin sensitivity and glucose utilization in lean and obese patients with type II diabetes.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
