    ) VALUES (1431562, 'Glimepiride', 'Aminoglutethimide', 'Minor', 'According to the manufacturer, aminoglutethimide may possibly induce the metabolism of antidiabetic agents. The clinical significance is unknown. Monitoring of glycemic control may be advisable during coadministration. Dose adjustments may be necessary if an interaction is suspected.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
