INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179635, 'Esterified estrogens', 'Metyrapone', 'Moderate', 'Estrogen therapy may reduce the response to metyrapone test. The mechanism is related to an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in decreased cortisol clearance. The increase in circulating cortisol may directly interfere with test results, since the pharmacologic effect of metyrapone is to inhibit adrenal production of cortisol and corticosterone in order to elicit an increase in ACTH production by the pituitary.', NULL, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', NULL, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179848/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[2] "Product Information. Femhrt (ethinyl estradiol-norethindrone)" Parke-Davis, Morris Plains, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365209);
