INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179903, 'Conjugated estrogens (topical)', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180116/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179904, 'Conjugated estrogens (topical)', 'Metyrapone', 'Moderate', 'Estrogen therapy may reduce the response to metyrapone test. The mechanism is related to an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in decreased cortisol clearance. The increase in circulating cortisol may directly interfere with test results, since the pharmacologic effect of metyrapone is to inhibit adrenal production of cortisol and corticosterone in order to elicit an increase in ACTH production by the pituitary.', NULL, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', NULL, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180117/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[2] "Product Information. Femhrt (ethinyl estradiol-norethindrone)" Parke-Davis, Morris Plains, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (179905, 'Conjugated estrogens (topical)', 'Prednisolone', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids. The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance. The interaction has been reported with estrogens or estrogen-containing oral contraceptives (OCs) and hydrocortisone, prednisone, and prednisolone.', NULL, 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180118/', '[1] Meffin PJ, Wing LM, Sallustio BC, Brooks PM "Alterations in prednisolone as a result of oral contraceptive use and dose." Br J Clin Pharmacol 17 (1984): 655-64[2] Legler UF, Benet LZ "Marked alterations in dose-dependent prednisolone kinetics in women taking oral contraceptives." Clin Pharmacol Ther 39 (1986): 425-9[3] Frey BM, Schaad HJ, Frey FJ "Pharmacokinetic interaction of contraceptive steroids with prednisone and prednisolone." Eur J Clin Pharmacol 26 (1984): 505-11[4] "Product Information. Premarin (conjugated estrogens)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] Olivesi A "Modified elimination of prednisolone in epileptic patients on carbamazepine monotherapy, and in women using low-dose oral contraceptives." Biomed Pharmacother 40 (1986): 301-8[6] "Product Information. Ortho-Novum (oral contraceptive combination pill)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] Boekenoogen SJ, Szefler SJ, Jusko WJ "Prednisolone disposition and protein binding in oral contraceptive users." J Clin Endocrinol Metab 56 (1983): 702-8[8] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[9] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767365209);
