INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180203, 'Etacrynic acid', 'Demeclocycline', 'Minor', 'The coadministration of diuretics and tetracyclines may result in decreased renal function manifested by increases in serum creatinine and blood urea nitrogen (BUN). Usually, no clinical intervention is necessary, unless decreases in renal function occur. If renal function deteriorates, discontinuation of one or both agents may be necessary.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180416/', '[1] Jick H, Slone D, Shapiro S, et al "Tetracycline and drug-attributed rises in blood urea nitrogen: a report from the Boston Collaborative Drug Surveillance Program." JAMA 220 (1972): 377-9[2] Alexander MR "Tetracycline and rises in urea nitrogen." JAMA 221 (1972): 713-4[3] Garty M, Hurwitz A "Effect of cimetidine and antacids on gastrointestinal absorption of tetracycline." Clin Pharmacol Ther 28 (1980): 203-7[4] Tannenberg AM "Tetracycline and rises in urea nitrogen." JAMA 221 (1972): 713[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180204, 'Etacrynic acid', 'Diflunisal', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', NULL, 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180417/', '[1] Muller FO, Schall R, Devaal AC, Groenewoud G, Hundt HKL, Middle MV "Influence of meloxicam on furosemide pharmacokinetics and pharmacodynamics in healthy volunteers." Eur J Clin Pharmacol 48 (1995