INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180359, 'Idelalisib', 'Ethionamide', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180572/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180360, 'Indium In-111 oxyquinoline', 'Ethionamide', 'Moderate', 'Certain medications may decrease chemotaxis and cause false-negative results with Indium In-111-labeled leukocyte studies.', NULL, 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', NULL, 'Clinicians should be aware of possible diagnostic interference in patients who are on long-term antibiotic treatment, corticosteroids, interleukin-2, parenteral nutrition, or high doses or supratherapeutic concentrations of lidocaine or procainamide.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180573/', '[1] "Product Information. Indium Oxyquinoline In-111 (indium oxyquinoline In-111)." GE Healthcare, Princeton, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180361, 'Infliximab', 'Ethionamide', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180574/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180362, 'Insulin human', 'Ethionamide', 'Moderate', 'The hypoglycemic effect of insulin may be potentiated by certain drugs, including ACE inhibitors, angiotensin receptor blockers (ARBs), 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinidine, quinine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, ARBs, fibrates, ginseng); stimulating insulin secretion (salicylates, disopyramide, pentoxifylline, propoxyphene, quinidine, quinine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Clinical hypoglycemia has been reported during use of some of these agents alone or with insulin and/or insulin secretagogues.', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment.', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment. The insulin dosage may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180575/', '[1] "Product Information. Apidra (insulin glulisine)." Aventis Pharmaceuticals, Bridgewater, NJ.[2] Semel JD, Wortham E, Karl DM "Fasting hypoglycemia associated with disopyramide." Am Heart J 106 (1983): 1160-1[3] Asplund K, Wiholm BE, Lithner F "Glibenclamide-associated hypoglycaemia: a report on 57 cases." Diabetologia 24 (1983): 412-7[4] Goldberg IJ, Brown LK, Rayfield EJ "Disopyramide (norpace)-induced hypoglycemia." Am J Med 69 (1980): 463-6[5] Croxson MS, Shaw DW, Henley PG, Gabriel HDLL "Disopyramide-induced hypoglycaemia and increased serum insulin." N Z Med J July (1987): 407-8[6] Herings RMC, Deboer A, Stricker BHC, Leufkens HGM, Porsius A "Hypoglycaemia associated with use of inhibitors of angiotensin converting enzyme." Lancet 345 (1995): 1195-8[7] Giugliano D, Ceriello A, Saccomanno F, et al "Effects of salicylate, tolbutamide, and prostaglandin E2 on insulin responses to glucose in noninsulin-dependent diabetes mellitus." J Clin Endocrinol Metab 61 (1985): 160-6[8] Aleyassine H, Gardiner RJ "Dual action of antidepressant drugs (MAO inhibitors) on insulin release." Endocrinology 96 (1975): 702-10[9] Baciewicz AM, Swafford WB Jr "Hypoglycemia induced by the interaction of chlorpropamide and co-trimoxazole." Drug Intell Clin Pharm 18 (1984): 309-10[10] Hellman B "Potentiating effects of drugs on the binding of glibenclamide to pancreatic beta cells." Metabolism 23 (1974): 839-46[11] Christensen LK, Hansen JM, Kristensen M "Sulphaphenazole-induced hypoglycemic attacks in tolbutamide-treated diabetics." Lancet 2 (1963): 1298-301[12] Darcy PF, Griffin JP "Interactions with drugs used in the treatment of depressive illness." Adverse Drug React Toxicol Rev 14 (1995): 211-31[13] Wiederholt IC, Genco M, Foley JM "Recurrent 