    ) VALUES (1431861, 'Vancomycin', 'Piperacillin', 'Major', 'When administered concomitantly, vancomycin and piperacillin may have additive nephrotoxic effects. A limited number of retrospective studies have found an increased incidence of acute kidney injury in patients treated with vancomycin and piperacillin-tazobactam relative to vancomycin alone. No pharmacokinetic interactions have been noted between these agents.', NULL, 'Caution is advised when vancomycin is used concomitantly with other nephrotoxic drugs such as piperacillin.', NULL, 'Caution is advised when vancomycin is used concomitantly with other nephrotoxic drugs such as piperacillin. Clinical and laboratory monitoring of renal function is recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
