    ) VALUES (1432010, 'Bretylium', 'Prilocaine', 'Moderate', 'Amide-type local anesthetics may have additive cardiac effects (e.g., bradycardia, chest pain, heart block, arrhythmias, ECG abnormalities, cardiac arrest) with class III antiarrhythmic agents.', NULL, 'Patients treated with class III antiarrhythmic agents (e.', NULL, 'Patients treated with class III antiarrhythmic agents (e.g., amiodarone, dofetilide, ibutilide, sotalol) should be under close surveillance during administration of amide-type local anesthetics. ECG monitoring should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
