    ) VALUES (1432052, 'Aluminum hydroxide', 'Promethazine', 'Minor', 'Coadministration with aluminum- or magnesium-containing antacids may decrease the serum concentrations of orally administered phenothiazines. The proposed mechanism is antacid adsorption resulting in reduced phenothiazine bioavailability.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
