INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180625, 'Etodolac', 'Diflunisal', 'Moderate', 'Concomitant use of more than one nonsteroidal anti-inflammatory drug (NSAID) at a time may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines. These events can occur at any time during NSAID use, with or without warning symptoms. The risk is dependent on both dosage and duration of therapy.', NULL, 'Concomitant use of more than one NSAID at a time should generally be avoided.', NULL, 'Concomitant use of more than one NSAID at a time should generally be avoided. Patients treated with an NSAID should be advised to take it with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, misoprostol, proton pump inhibitors) may be considered in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180839/', '[1] "Product Information. Duexis (famotidine-ibuprofen)." Horizon Pharma USA Inc, Northbrook, IL.[2] "Product Information. Mobic (meloxicam)" Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Ponstel (mefenamic acid)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] "Product Information. Naprosyn (naproxen)." Syntex Laboratories Inc, Palo Alto, CA.[5] "Product Information. VIMOVO (esomeprazole-naproxen)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[6] "Product Information. Caldolor (ibuprofen)." Cumberland Pharmaceuticals Inc, Nashville, TN.[7] "Product Information. Meclofenamate Sodium (meclofenamate)." Mylan Pharmaceuticals Inc, Morgantown, WV.[8] "Product Information. Lodine (etodolac)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[9] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[10] "Product Information. Anaprox (naproxen)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Nalfon (fenoprofen)." Xspire Pharma, Ridgeland, MS.[12] "Product Information. Etodolac ER (etodolac)." Taro Pharmaceuticals U.S.A. Inc, Hawthorne, NY.[13] "Product Information. Cambia (diclofenac)." Kowa Pharmaceuticals America (formerly ProEthic), Montgomery, AL.[14] "Product Information. Ketoprofen ER (ketoprofen)." Mylan Pharmaceuticals Inc, Morgantown, WV.[15] "Product Information. Indocin (indomethacin)." Merck & Co, Inc, West Point, PA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180626, 'Etodolac', 'Drotrecogin alfa', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT).', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia.', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia. Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180840/', '[1] "Product Information. Xigris (drotrecogin alfa)." Lilly, Eli and Company, Indianapolis, IN.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180627, 'Etodolac', 'Enoxacin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180841/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180628, 'Everolimus', 'Etodolac', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180842/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Sheiner PA, Mor E, Chodoff L, Glabman S, Emre S, Schwartz ME, Miller CM "Acute renal, failure associated with the use of ibuprofen in two liver transplant recipients on FK506." Transplantation 57 (1994): 1132-3[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180629, 'Exenatide', 'Etodolac', 'Moderate', 'Drugs that affect renal function and/or hydration status may potentiate the adverse renal effects of exenatide. The mechanism of interaction is unknown. Exenatide has not been found to be directly nephrotoxic in preclinical or clinical studies.', NULL, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', NULL, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180843/', '[1] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.[2] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.', 1767363026);
