INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180642, 'Gentamicin', 'Etodolac', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180856/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180643, 'Glipizide', 'Etodolac', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral 