INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (180837, 'Amprenavir', 'Etravirine', 'Moderate', 'Coadministration of etravirine and protease inhibitors without concomitant ritonavir as a pharmacokinetic booster may produce significant alterations in plasma concentrations of the protease inhibitor. Etravirine is an inducer of CYP450 3A4 and inhibitor of CYP450 2C19 and 2C9, thus it may variously affect the metabolism and clearance of protease inhibitors. In unboosted pharmacokinetic regimens, etravirine has reduced plasma levels of atazanavir and indinavir and increased levels of amprenavir and nelfinavir. However, significant interactions affecting either etravirine and/or protease inhibitor plasma concentrations have also been observed with boosted regimens, including atazanavir/ritonavir, fosamprenavir/ritonavir, and tipranavir/ritonavir.', NULL, 'Protease inhibitors should not be prescribed with etravirine in the absence of low-dose ritonavir as a pharmacokinetic booster.', NULL, 'Protease inhibitors should not be prescribed with etravirine in the absence of low-dose ritonavir as a pharmacokinetic booster.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181051/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[3] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman