INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181112, 'Exemestane', 'Echinacea', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'Darolutamide, Anastrozole, Aminoglutethimide, Fulvestrant, Abarelix, Relugolix, Bicalutamide, Degarelix, Nilutamide, Apalutamide', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181326/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181113, 'Exemestane', 'Estrone', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer. Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', 'Bazedoxifene', 'Darolutamide, Tamoxifen, Fulvestrant, Toremifene, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, Abiraterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181327/', '[1] "Product Information. Arimidex (anastrozole)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Aromasin (exemestane)" Pharmacia & Upjohn, Kalamazoo, MI.[3] "Product Information. Femara (letrozole)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
