INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181346, 'Factor IX Complex (Human)', 'Aprotinin', 'Major', 'Due to additive or synergistic pharmacodynamic effects, the risk of thrombosis may be increased when antifibrinolytic agents (e.g., aminocaproic acid, aprotinin, tranexamic acid) are coadministered with other hemostatic agents such as anti-inhibitor coagulant complex, factor IX complex, or prothrombin complex.', NULL, 'Concomitant administration of antifibrinolytic agents with other hemostatic agents should generally be avoided.', NULL, 'Concomitant administration of antifibrinolytic agents with other hemostatic agents should generally be avoided. Consult the manufacturer''s product labeling for recommended dosing intervals between products.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181560/', '[1] "Product Information. LYSTEDA (tranexamic acid)." Xanodyne Pharmaceuticals Inc, Newport, KY.[2] "Product Information. Amicar (aminocaproic acid)." Immunex Corporation, Seattle, WA.[3] "Product Information. Cyklokapron (tranexamic acid)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
