INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181351, 'Amprenavir', 'Calcium carbonate', 'Moderate', 'INTERVAL: Concurrent administration of antacids or other agents with acid-neutralizing effects may decrease the oral bioavailability of amprenavir and reduce its concentrations in plasma. Amprenavir solubility decreases with increasing pH, thus reduction in gastric acidity may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance. In contrast, the prodrug fosamprenavir appears to be less affected by antacids.', NULL, 'Amprenavir should be administered at least 1 hour before or after antacids or other oral preparations containing antacids (e.', NULL, 'Amprenavir should be administered at least 1 hour before or after antacids or other oral preparations containing antacids (e.g., didanosine buffered tablets or pediatric oral solution).', 'Absorption', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Etravirine, More', 'Calcium glucoheptonate, Calcium lactate, Calcium chloride, Calcium gluconate, Calcium glubionate anhydrous, Calcium citrate, Calcium Phosphate, Ascorbic acid, Calcium chloride, Calcium citrate, Calcium acetate, Calcium Phosphate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181565/', '[1] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[2] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.