INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181477, 'Ibrutinib', 'Fedratinib', 'Major', 'Coadministration of ibrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ibrutinib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ibrutinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', 'Futibatinib, Fedratinib, Copanlisib, Alpelisib, Vemurafenib, Erlotinib, Lenvatinib, Erdafitinib, Vandetanib, Trametinib, Afatinib, More', 'Dabrafenib, Dacomitinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Tivozanib, Brigatinib, Axitinib, Lenvatinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181691/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.', 17