INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181481, 'Iloprost', 'Fedratinib', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Streptokinase, Eptifibatide, Selexipag, Abciximab, Dicoumarol, Tenecteplase, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Larotrectinib, Bosutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181695/', '[1] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (181482, 'Imatinib', 'Fedratinib', 'Moderate', 'Imatinib is a substrate of the CYP450 3A4 isoenzyme as well as a potent competitive inhibitor of the CYP450 2C9, 2D6 and 3A4 isoenzymes. Some drugs that are known inhibitors of CYP450 3A4 are also metabolized by one or more of the isoenzymes inhibited by imatinib. Theoretically, coadministration of imatinib with those drugs may result in mutually elevated plasma drug concentrations due to competitive and noncompetitive inhibition of CYP450 activities.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of imatinib, including serious adverse effects such as edema, hematologic toxicity and immunosuppression, should be considered during concomitant therapy, particularly with potent CYP450 3A4 inhibitors such as ketoconazole, itraconazole, ritonavir, nefazodone, and erythromycin. In addition, clinical and laboratory monitoring for potentially increased pharmacologic effects of coadministered medications is recommended, especially those with a narrow therapeutic range.', 'Metabolism', 'Futibatinib, Fedratinib, Copanlisib, Alpelisib, Vemurafenib, Erlotinib, Lenvatinib, Erdafitinib, Vandetanib, Trametinib, Afatinib, More', 'Sunitinib, Regorafenib, Imatinib, Lenvatinib, Erlotinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181696/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
